                        config.exportServer,
                        {
                            method : 'POST',
                            body   : JSON.stringify({
                                html        : pages,
                                orientation : config.orientation,
                                format      : config.paperFormat,
                                fileFormat  : config.fileFormat,
                                fileName    : config.fileName,
                                clientURL   : config.clientURL
                            }),
                            credentials : 'omit',
                            headers     : {
                                'Content-Type' : 'application/json'
                            },
                            parseJson : true
                        }
