        me.mask = new Mask({
            progress    : 0,
            maxProgress : 100,
            text        : 'Generating pages...',
            element     : me.exportDialog.element
        });

        const detacher = me.on({
            export() {
                me.mask.close();
                detacher();
            },
            exportstep({ progress, text }) {
                me.mask.progress = progress;
                me.mask.text = text;
            }
        });

        me.currentExportPromise = me.export(values);

