            horizontalscroll       : me.onHorizontalScroll,
            svgcanvascreated       : me.createMarkers,
            togglenode             : me.onToggleNode,
            scroll                 : me.onVerticalScroll,
            //            eventlayout      : me.onEventLayout,
            timelineviewportresize : me.onViewportResize,
            thisObj                : me
